/* RowScroll 1.0, https://github.com/ericbutler555/row-scroll */
/*can edit->*/ var scrollTargets = ".scroll-target", wiggleRoom = 10, scrollSpeed = 400;
$.extend($.easing,{easeOutQuad:function(n,e,i,u,s){return-u*(e/=s)*(e-2)+i}});
$(window).on("load",function(){var o=$(scrollTargets),t=[],e=wiggleRoom,n=scrollSpeed;$(scrollTargets).each(function(){t.push(Math.ceil($(this).offset().top))}),$(window).on("resize",function(){t.length=0,$(scrollTargets).each(function(){t.push(Math.ceil($(this).offset().top))})}),$(window).on("mousewheel",function(l){var a=$(window).scrollTop();if(l.deltaY>0)var i="up";else if(l.deltaY<=0)var i="down";if("down"==i){for(var f=0;f<o.length;f++)if(a>=t[f]-e&&a<t[f+1]-e){l.preventDefault(),$("html, body").stop().animate({scrollTop:$(o[f+1]).offset().top},n,"easeOutQuad");break}}else if("up"==i)for(var f=0;f<o.length;f++)if(a<=t[f+1]+e&&a>t[f]+e){l.preventDefault(),$("html, body").stop().animate({scrollTop:$(o[f]).offset().top},n,"easeOutQuad");break}return!1})});